mapper.select:

SELECT *
  FROM(
	SELECT /*+ INDEX_DESC(tb pk_board) */ rownum rn, tb.* 
	  FROM TBL_BOARD tb
	 WHERE rownum <= #{pageNum} * #{amount}
 ) 
 WHERE rn > (#{pageNum} - 1) * #{amount}

mapper.update:
update tbl_board
   set title = #{title}, content=#{content}, updatedate = sysdate
 where bno = #{bno}
